import React, { useEffect, useState } from "react";
import { css } from "@emotion/css";
// import styled from "@emotion/styled";
// import Select from "react-select";
import Card from "./Card";
import axios from "axios";

// const options = [
//   { value: "Rock", label: "Rock" },
//   { value: "Pop music", label: "Pop music" },
//   { value: "Hip hop music", label: "Hip hop music" },
//   { value: "Rhythm and blues", label: "Rhythm and blues" },
//   { value: "Blues", label: "Blues" },
//   { value: "Jazz", label: "Jazz" },
//   { value: "Classical music", label: "Classical music" },
//   { value: "Reggae", label: "Reggae" },
//   { value: "Funk", label: "Funk" },
// ];
function NewMusic({ musics }) {
  const [addMusic, setAddMusic] = useState({
    title: "",
    artist: "",
    genre: "",
    album: false,
  });
  useEffect(() => {}, []);
  const handleChange = (e) => {
    setAddMusic({
      ...addMusic,
      [e.target.name]: e.target.value,
    });
  };
  const handleSubmit = (e) => {
    e.preventDefault();
    axios.post();
    console.log(addMusic);
    setAddMusic({
      ...addMusic,
      title: "",
      artist: "",
      genre: "",
      album: false,
    });
    axios
      .post("http://localhost:5000/create", {
        title: addMusic.title,
        artist: addMusic.artist,
        genre: addMusic.genre,
        album: addMusic.album,
      })
      .then((res) => console.log(res.data))
      .catch((err) => console.log(err));
  };
  return (
    <div
      className={css`
        display: flex;
        flex-wrap: wrap;
        justify-content: center; ;
      `}
    >
      {musics.map(({ _id, ...others }) => (
        <Card key={_id} {...others} />
      ))}

      <form
        className={css`
          display: flex;
          flex-direction: column;
        `}
        onSubmit={handleSubmit}
      >
        <input
          name="title"
          onChange={handleChange}
          required
          type="text"
          placeholder="Title"
          value={addMusic.title}
        />
        <input
          name="artist"
          onChange={handleChange}
          type="text"
          required
          placeholder="Artist"
          value={addMusic.artist}
        />
        <input
          name="genre"
          onChange={handleChange}
          type="text"
          required
          placeholder="Genre"
          value={addMusic.genre}
        />
        <input
          name="album"
          onChange={handleChange}
          type="checkbox"
          placeholder="Genre"
          value={addMusic.album}
        />
        <button type="submit">Add</button>
      </form>
    </div>
  );
}

export default NewMusic;
