import axios from "axios";
import { call, put, takeEvery } from "redux-saga/effects";
import { getMusic } from "./features/music/musicSlice";
function* getFetchedMusic() {
  const music = yield call(() =>
    axios.get("https://jsonplaceholder.typicode.com/posts")
  );
  const formattedData = yield music.json();
  yield put(getMusic(formattedData));
  //   console.log(formattedData, "formattedData");
}
function* musicSaga() {
  yield takeEvery("music/getMusic", getFetchedMusic);
}
export default musicSaga;
