import axios from "axios";

axios.defaults.baseURL = "http://localhost:5000";

export const getMusicAPI = async () => axios.get("/");

export const createMusicAPI = async (music) => axios.post(`/users`, music);

export const updateMusicAPI = async (id) => axios.post("/update", { id });

export const deleteMusicByIdAPI = async (id) => axios.post("/delete", { id });
