import React, { useEffect, useState } from "react";
import { css } from "@emotion/css";
import styled from "@emotion/styled";

import Card from "./Card";

function NewMusic() {
  const [addMusic, setAddMusic] = useState({
    title: "",
    artist: "",
    genre: "",
    Album: false,
  });
  useEffect(() => {}, []);
  const handleChange = (e) => {
    setAddMusic({
      ...addMusic,
      [e.target.name]: e.target.value,
    });
  };
  const handleSubmit = (e) => {
    e.preventDefault();
  };
  return (
    <div
      className={css`
        display: flex;
        flex-wrap: wrap;
        justify-content: center; ;
      `}
    >
      <Card />
      <Card />
      <Card />
      <Card />
      <Card />
      <form
        className={css`
          display: flex;
          flex-direction: column;
        `}
        onSubmit={handleSubmit}
      >
        <input
          name="title"
          onChange={handleChange}
          type="text"
          placeholder="Title"
        />
        <input
          name="artist"
          onChange={handleChange}
          type="text"
          placeholder="Artist"
        />
        <input
          name="genre"
          onChange={handleChange}
          type="text"
          placeholder="Genre"
        />
        <input
          onChange={handleChange}
          type="checkbox"
          name="album"
          placeholder="Album"
        />
        <button>Add</button>
      </form>
    </div>
  );
}

export default NewMusic;
